/**
 * Border style.
 *
 * @since 1.0.0
 */
export type BorderStyle = 'box' | 'round' | 'thick';

/**
 * Item pretty names.
 *
 * @since 1.0.0
 */
export type ItemPrettyNames = Record<string, string>;

/**
 * Log level.
 *
 * @since 1.0.0
 */
export type LogLevel =
  'debug'
  | 'info'
  | 'warn'
  | 'error';

/**
 * Text align.
 *
 * @since 1.0.0
 */
export type TextAlign = 'left' | 'center' | 'right';
